import React, { useState } from 'react';
import { Search, ChevronRight, Database, Sun, Home, Activity, 
  BarChart2, Building2, Gauge, Cloud, Leaf } from 'lucide-react';

const DatasetPortal = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  const categories = [
    {
      id: 1,
      title: "Research Papers",
      icon: <Database className="w-6 h-6" />,
      description: "Academic research papers from IEEE, ArXiv, and MDPI journals"
    },
    {
      id: 2,
      title: "Solar Production",
      icon: <Sun className="w-6 h-6" />,
      description: "Solar generation data and geographical information systems"
    },
    {
      id: 3,
      title: "Energy Consumption",
      icon: <Activity className="w-6 h-6" />,
      description: "Residential and commercial energy consumption datasets"
    },
    {
      id: 4,
      title: "Load Monitoring",
      icon: <BarChart2 className="w-6 h-6" />,
      description: "NILM datasets and appliance energy usage monitoring"
    },
    {
      id: 5,
      title: "Building Energy",
      icon: <Building2 className="w-6 h-6" />,
      description: "Building energy data and efficiency metrics"
    },
    {
      id: 6,
      title: "Smart Meters",
      icon: <Gauge className="w-6 h-6" />,
      description: "Smart meter data and implementation resources"
    },
    {
      id: 7,
      title: "Environmental",
      icon: <Cloud className="w-6 h-6" />,
      description: "Climate and environmental impact data"
    },
    {
      id: 8,
      title: "Energy Tools",
      icon: <Leaf className="w-6 h-6" />,
      description: "Energy data portals and analysis tools"
    }
  ];

  const datasets = {
    1: [
      "IEEE Paper: Document 9826532",
      "ArXiv Paper: 2307.00142",
      "IEEE Paper: Document 10387673",
      "ArXiv Paper: 2102.12578",
      "MDPI Energies Journal Articles"
    ],
    2: [
      "Photovoltaic Geographical Information System (PVGIS)",
      "National Solar Radiation Database (NSRDB) - NREL",
      "EMHIRES Solar Generation Time Series",
      "Solcast Data Specifications",
      "NASA POWER Project"
    ],
    3: [
      "EIA Residential Consumption Data 2020",
      "Building Data Genome 2",
      "UK-DALE Disaggregated Dataset",
      "Smart Meter Energy Use Data",
      "Electric Power Consumption Dataset"
    ],
    4: [
      "UK-DALE Disaggregated Dataset",
      "GitHub: NILM Tools and Resources",
      "SHEd Dataset - NILM",
      "Household Appliances Power Consumption",
      "Appliances Energy Prediction Dataset"
    ],
    5: [
      "Better Buildings Solution Center",
      "Building Data Genome 2",
      "NREL Buildings Benchmark",
      "EnergySage - Whole Home Solutions",
      "Intelligent Energy Management System"
    ],
    6: [
      "Smart Meter Energy Use Data in London",
      "CRU: Smart Meters and Services",
      "OpenSynthS GB Smart Meter Synthetic Data"
    ],
    7: [
      "International Renewable Energy Agency (IRENA)",
      "GitHub: ClimateSet by RolnickLab",
      "CDP Data Portal"
    ],
    8: [
      "Global Energy Monitor",
      "Utility Rate Database - OpenEI",
      "Better Buildings Solution Center",
      "NREL Data and Tools",
      "U.S. Energy Information Administration (EIA)"
    ]
  };

  return (
    <div className="min-h-screen bg-black text-white">
      {/* Hero Section */}
      <div className="relative h-96 flex items-center justify-center overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-r from-blue-900 to-purple-900 opacity-50" />
        <div className="relative z-10 text-center px-4">
          <h1 className="text-4xl md:text-6xl font-bold mb-6">Energy Dataset Portal</h1>
          <p className="text-xl md:text-2xl text-gray-200 max-w-2xl mx-auto">
            AI Power Solar Energy Optimization LLM Integration DATASETS - Graduation Project 2024-2025
          </p>
        </div>
      </div>

      {/* Search Bar */}
      <div className="max-w-4xl mx-auto px-4 -mt-8 relative z-20">
        <div className="bg-white/10 backdrop-blur-lg rounded-lg p-2 flex items-center">
          <Search className="w-6 h-6 text-gray-400 ml-2" />
          <input
            type="text"
            placeholder="Search datasets..."
            className="w-full bg-transparent border-none focus:outline-none text-white px-4 py-2"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      {/* Categories Grid */}
      <div className="max-w-6xl mx-auto px-4 py-16">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {categories.map((category) => (
            <button
              key={category.id}
              onClick={() => setSelectedCategory(category.id)}
              className={`p-6 rounded-lg transition-all duration-300 ${
                selectedCategory === category.id
                  ? 'bg-white/20 backdrop-blur-lg'
                  : 'bg-white/10 hover:bg-white/15'
              }`}
            >
              <div className="flex items-center justify-between mb-4">
                {category.icon}
                <ChevronRight className="w-5 h-5 text-gray-400" />
              </div>
              <h3 className="text-lg font-semibold mb-2">{category.title}</h3>
              <p className="text-sm text-gray-400">{category.description}</p>
            </button>
          ))}
        </div>
      </div>

      {/* Dataset List */}
      {selectedCategory && (
        <div className="max-w-4xl mx-auto px-4 pb-16">
          <div className="bg-white/10 backdrop-blur-lg rounded-lg p-6">
            <h2 className="text-2xl font-bold mb-6">
              {categories.find(c => c.id === selectedCategory)?.title} Resources
            </h2>
            <div className="space-y-4">
              {datasets[selectedCategory].map((dataset, index) => (
                <div
                  key={index}
                  className="p-4 bg-white/5 rounded-lg hover:bg-white/10 transition-colors cursor-pointer flex items-center justify-between"
                >
                  <span>{dataset}</span>
                  <ChevronRight className="w-5 h-5 text-gray-400" />
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DatasetPortal;
