type LinkType = 'ieee' | 'arxiv' | 'mdpi' | 'tool' | 'database' | 'dataset' | 'specification';

export interface Link {
  title: string;
  url: string;
  type: LinkType;
}

export interface Category {
  id: number;
  title: string;
  icon: string;
  description: string;
  links: Link[];
}

export interface Categories {
  categories: Category[];
}
